public class HowForLoopWork_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int i=0; i<=5;i++) { // int i=0; i<=5 this steps will be executed first, but i ++ happens at the end of the loop
			
			System.out.println(i);
			
			//i++ of the loops is execute a the end of the loop
		}

	}

}
